-- таблица наших сериалов
DROP TABLE IF EXISTS series_TV;
CREATE TABLE series_TV (
	id BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,
  	name VARCHAR(255) NOT NULL, -- названия у разных сериалов могут быть одинаковые
  	original_name VARCHAR(255) DEFAULT NULL, -- название на языке оригинала    
  	counter_episodes SMALLINT DEFAULT NULL, -- подразумеваем что если значение 0 = хотя бы одна серия
  	age TINYINT DEFAULT 0, -- возрастной цензор (со скольки лет можно смотреть)
  	max_counter_season TINYINT NOT NULL, -- макс. кол-во сезонов 
  	description TEXT DEFAULT NULL, -- описание
  	rating TINYINT UNSIGNED DEFAULT NULL, -- НА ДАННЫЙ МОМЕНТ рейтинг до 100. позже триером мы его обрежем до 100
